mapper.select:
     select filename
		from board
		where num=#{num}

mapper.update:
update board 
set re_step=re_step+1
where ref=#{ref} and re_step >= #{re_step}

update board
set writer=#{writer}, subject=#{subject}
	, content=#{content}, filename=#{filename}
	, filesize=#{filesize}, down=#{down}
where num=#{num}
